-- 移动平均线策略
INSERT IGNORE INTO `strategy_info` (`strategy_code`, `strategy_name`, `description`, `params_desc`, `default_params`, `category`, `create_time`, `update_time`,`comments`) VALUES
('SMA', N'简单移动平均线策略', N'基于简单移动平均线的交叉信号生成买卖决策', N'{"shortPeriod":"短期均线周期","longPeriod":"长期均线周期"}', '{"shortPeriod":9,"longPeriod":26}', N'移动平均线策略', NOW(), NOW(), N'【使用场景】趋势性强的市场，中长线投资者。【优点】信号明确，易于理解和执行，在强趋势中表现优异。【缺点】滞后性明显，震荡市场容易产生假信号。【历史表现】在牛市中夏普比率可达1.2-1.5，最大回撤通常10-15%。【风险提示】不适合快速变化的市场。'),
('EMA', N'指数移动平均线策略', N'基于指数移动平均线的交叉信号生成买卖决策', N'{"shortPeriod":"短期均线周期","longPeriod":"长期均线周期"}', '{"shortPeriod":9,"longPeriod":26}', N'移动平均线策略', NOW(), NOW(), N'【使用场景】需要快速响应价格变化的市场。【优点】对最新价格反应敏感，减少滞后性。【缺点】容易产生假信号，需要严格的风控。【历史表现】夏普比率通常0.8-1.3，最大回撤8-20%。【适用性】适合短中线交易，在波动市场中需谨慎使用。'),
('WMA', N'加权移动平均线策略', N'基于加权移动平均线的交叉信号生成买卖决策', N'{"shortPeriod":"短期均线周期","longPeriod":"长期均线周期"}', '{"shortPeriod":9,"longPeriod":26}', N'移动平均线策略', NOW(), NOW(), N'【使用场景】希望平衡响应速度和稳定性的投资者。【优点】比SMA敏感，比EMA稳定。【缺点】参数调整复杂，效果提升有限。【历史表现】夏普比率0.9-1.2，最大回撤10-18%。【评价】实用性一般，多用于组合策略。'),
('HMA', N'赫尔移动平均线策略', N'基于赫尔移动平均线的交叉信号生成买卖决策', N'{"shortPeriod":"短期均线周期","longPeriod":"长期均线周期"}', '{"shortPeriod":9,"longPeriod":26}', N'移动平均线策略', NOW(), NOW(), N'【使用场景】需要减少滞后但保持平滑的快速市场。【优点】显著减少滞后性，保持良好的平滑性。【缺点】计算复杂，在极端市场可能失效。【历史表现】夏普比率1.0-1.4，最大回撤5-15%。【特色】在快速趋势变化中表现优异。'),
('KAMA', N'考夫曼自适应移动平均线策略', N'基于考夫曼自适应移动平均线的交叉信号生成买卖决策', N'{"shortPeriod":"短期均线周期","longPeriod":"长期均线周期","effRatioShortPeriod":"效率比率短期","effRatioLongPeriod":"效率比率长期"}', '{"shortPeriod":9,"longPeriod":26,"effRatioShortPeriod":2,"effRatioLongPeriod":30}', N'移动平均线策略', NOW(), NOW(), N'【使用场景】复杂多变的市场环境，需要自适应调整的策略。【优点】根据市场效率自动调整敏感度，趋势市场快速跟踪，震荡市场保持稳定。【缺点】参数较多，计算复杂。【历史表现】夏普比率1.1-1.6，最大回撤6-12%。【评级】高级策略，适合有经验的投资者。'),
('ZLEMA', N'零滞后指数移动平均线策略', N'基于零滞后指数移动平均线的交叉信号生成买卖决策', N'{"shortPeriod":"短期均线周期","longPeriod":"长期均线周期"}', '{"shortPeriod":9,"longPeriod":26}', N'移动平均线策略', NOW(), NOW(), N'【使用场景】快速交易，短线投机。【优点】显著减少滞后性，提供及时信号。【缺点】假信号增多，需要严格风控。【历史表现】夏普比率0.6-1.2，最大回撤可达25%。【风险等级】高风险高收益，需要丰富的交易经验。'),
('DEMA', N'双重指数移动平均线策略', N'基于双重指数移动平均线的交叉信号生成买卖决策', N'{"shortPeriod":"短期均线周期","longPeriod":"长期均线周期"}', '{"shortPeriod":9,"longPeriod":26}', N'移动平均线策略', NOW(), NOW(), N'【使用场景】需要比EMA更快响应但保持稳定性的市场。【优点】减少滞后性，保持相对稳定。【缺点】在震荡市场效果一般。【历史表现】夏普比率0.9-1.3，最大回撤8-16%。【适用性】中等风险策略，适合有一定经验的投资者。'),
('TEMA', N'三重指数移动平均线策略', N'基于三重指数移动平均线的交叉信号生成买卖决策', N'{"shortPeriod":"短期均线周期","longPeriod":"长期均线周期"}', '{"shortPeriod":9,"longPeriod":26}', N'移动平均线策略', NOW(), NOW(), N'【使用场景】短线交易，追求最小滞后性。【优点】最小化滞后性，快速响应趋势变化。【缺点】信号敏感，假突破较多。【历史表现】夏普比率0.7-1.4，最大回撤可达20%。【建议】适合专业交易者，需配合其他过滤条件。'),
('VWAP', N'成交量加权平均价格策略', N'基于成交量加权平均价格的交叉信号生成买卖决策', N'{"period":"计算周期"}', '{"period":14}', N'移动平均线策略', NOW(), NOW(), N'【使用场景】日内交易，机构投资者基准。【优点】反映真实市场成本，结合成交量信息。【缺点】主要适用于日内，长期应用效果有限。【历史表现】日内夏普比率可达2.0+，但隔夜风险较大。【特色】机构常用基准，适合大额交易执行。'),
-- 新增移动平均线策略
('TRIMA', N'三角移动平均线策略', N'基于三角移动平均线的交叉信号生成买卖决策，对价格变化的反应比简单移动平均线更平滑', N'{"shortPeriod":"短期均线周期","longPeriod":"长期均线周期"}', '{"shortPeriod":9,"longPeriod":21}', N'移动平均线策略', NOW(), NOW(), N'【使用场景】震荡市场，追求信号稳定性的投资者。【优点】双重平滑处理，有效减少噪音和假信号。【缺点】反应较慢，可能错过快速趋势。【历史表现】夏普比率0.8-1.1，最大回撤8-12%。【特色】在横盘整理阶段表现稳定。'),
('T3', N'T3移动平均线策略', N'基于T3三重指数平滑移动平均线的交叉信号生成买卖决策，提供更平滑的价格曲线', N'{"period":"计算周期","volumeFactor":"体积因子"}', '{"period":10,"volumeFactor":0.7}', N'移动平均线策略', NOW(), NOW(), N'【使用场景】中线趋势跟踪，需要平衡平滑性和响应性。【优点】可调节体积因子来控制敏感度，三重平滑减少噪音。【缺点】参数优化复杂。【历史表现】夏普比率1.0-1.3，最大回撤6-14%。【评价】高级平滑算法，适合技术分析专家。'),
('MAMA', N'MESA自适应移动平均线策略', N'基于MESA自适应移动平均线的交叉信号生成买卖决策，能够根据市场条件自动调整', N'{"fastLimit":"快速限制","slowLimit":"慢速限制"}', '{"fastLimit":0.5,"slowLimit":0.05}', N'移动平均线策略', NOW(), NOW(), N'【使用场景】复杂多变的市场环境，需要智能适应的策略。【优点】根据市场周期自动调整参数，在不同市场状态下都能保持较好表现。【缺点】算法复杂，计算量大。【历史表现】夏普比率1.2-1.7，最大回撤5-10%。【评级】顶级自适应策略。'),
('VIDYA', N'可变指数动态平均线策略', N'基于可变指数动态平均线的交叉信号生成买卖决策，在波动较大时反应更快', N'{"shortCMAPeriod":"短期CMA周期","longCMAPeriod":"长期CMA周期","alpha":"Alpha参数"}', '{"shortCMAPeriod":9,"longCMAPeriod":12,"alpha":0.2}', N'移动平均线策略', NOW(), NOW(), N'【使用场景】波动性变化较大的市场。【优点】根据价格波动性智能调整权重，高波动时反应敏感，低波动时保持稳定。【缺点】在极端市场可能过度反应。【历史表现】夏普比率1.0-1.4，最大回撤7-15%。【适用性】适合波动率交易策略。'),
('WILDERS', N'威尔德平滑移动平均线策略', N'基于威尔德平滑移动平均线的交叉信号生成买卖决策，特殊的指数移动平均线', N'{"period":"计算周期"}', '{"period":14}', N'移动平均线策略', NOW(), NOW(), N'【使用场景】作为其他技术指标的基础组件，如RSI计算。【优点】威尔德专用算法，信号平滑稳定，减少市场噪音。【缺点】主要用于辅助计算，独立使用效果一般。【历史表现】夏普比率0.7-1.0，最大回撤10-18%。【定位】技术指标基础组件。'),

-- 震荡指标策略
('RSI', N'相对强弱指数策略', N'基于相对强弱指数的超买超卖信号生成买卖决策', N'{"period":"RSI计算周期","overSoldThreshold":"超卖阈值","overBoughtThreshold":"超买阈值"}', '{"period":14,"overSoldThreshold":30,"overBoughtThreshold":70}', N'震荡指标策略', NOW(), NOW(), N'【使用场景】震荡市场，反转交易，超买超卖判断。【优点】信号清晰，易于理解，历史验证充分。【缺点】在强趋势中可能长期处于超买超卖状态。【历史表现】夏普比率0.8-1.2，最大回撤15-25%。【胜率】约55-65%，经典可靠指标。'),
('STOCHASTIC', N'随机指标策略', N'基于随机指标的超买超卖信号生成买卖决策', N'{"kPeriod":"K值周期","dPeriod":"D值周期","overSoldThreshold":"超卖阈值","overBoughtThreshold":"超买阈值"}', '{"kPeriod":14,"dPeriod":3,"overSoldThreshold":20,"overBoughtThreshold":80}', N'震荡指标策略', NOW(), NOW(), N'【使用场景】短期反转机会捕捉，快速交易。【优点】敏感度高，能提供较早的信号。【缺点】假信号较多，需要过滤条件。【历史表现】夏普比率0.6-1.0，最大回撤20-30%。【特色】适合高频交易者，需要严格风控。'),
('STOCHASTIC_RSI', N'随机相对强弱指数策略', N'结合随机指标和RSI的优势，提供更敏感的超买超卖信号', N'{"rsiPeriod":"RSI计算周期","stochasticPeriod":"随机指标周期","kPeriod":"K值周期","dPeriod":"D值周期","overSoldThreshold":"超卖阈值","overBoughtThreshold":"超买阈值"}', '{"rsiPeriod":14,"stochasticPeriod":14,"kPeriod":3,"dPeriod":3,"overSoldThreshold":20,"overBoughtThreshold":80}', N'震荡指标策略', NOW(), NOW(), N'【使用场景】需要更敏感信号的短线交易。【优点】结合两种指标优势，提供更早的反转信号。【缺点】过度敏感，假信号增多。【历史表现】夏普比率0.5-1.1，最大回撤25-35%。【风险等级】中高风险，需要经验丰富的交易者。'),
('CCI', N'商品通道指数策略', N'基于商品通道指数的超买超卖信号生成买卖决策', N'{"period":"CCI计算周期","overSoldThreshold":"超卖阈值","overBoughtThreshold":"超买阈值"}', '{"period":20,"overSoldThreshold":-100,"overBoughtThreshold":100}', N'震荡指标策略', NOW(), NOW(), N'【使用场景】识别极端价格水平，商品期货交易。【优点】能够识别异常的价格偏离，适合商品市场。【缺点】在股票市场应用效果一般。【历史表现】商品市场夏普比率0.9-1.3，股票市场0.6-0.9，最大回撤18-28%。【特色】商品交易专用指标。'),
('WILLIAMS_R', N'威廉指标策略', N'基于威廉指标的超买超卖信号生成买卖决策', N'{"period":"计算周期","overSoldThreshold":"超卖阈值","overBoughtThreshold":"超买阈值"}', '{"period":14,"overSoldThreshold":-80,"overBoughtThreshold":-20}', N'震荡指标策略', NOW(), NOW(), N'【使用场景】短线交易，快速反转识别。【优点】反应极其敏感，能抓住快速反转机会。【缺点】噪音大，需要结合其他指标过滤。【历史表现】夏普比率0.4-0.9，最大回撤30-40%。【定位】辅助指标，不建议单独使用。'),
('CMO', N'钱德动量摆动指标策略', N'基于钱德动量摆动指标的超买超卖信号生成买卖决策', N'{"period":"计算周期","overSoldThreshold":"超卖阈值","overBoughtThreshold":"超买阈值"}', '{"period":14,"overSoldThreshold":-50,"overBoughtThreshold":50}', N'震荡指标策略', NOW(), NOW(), N'【使用场景】趋势确认，动量分析。【优点】衡量价格动量，避免长期停滞影响。【缺点】在震荡市场信号不够明确。【历史表现】夏普比率0.7-1.1，最大回撤18-25%。【适用性】适合趋势跟踪策略组合。'),
('ROC', N'变动率指标策略', N'基于价格变动率的超买超卖信号生成买卖决策', N'{"period":"计算周期","overSoldThreshold":"超卖阈值","overBoughtThreshold":"超买阈值"}', '{"period":12,"overSoldThreshold":-5,"overBoughtThreshold":5}', N'震荡指标策略', NOW(), NOW(), N'【使用场景】识别价格变化速度，动量分析。【优点】简单有效，计算快速。【缺点】容易受短期波动影响。【历史表现】夏普比率0.6-1.0，最大回撤20-30%。【定位】辅助指标，常与其他指标组合使用。'),
('PPO', N'百分比价格震荡指标策略', N'基于百分比价格震荡指标的超买超卖信号生成买卖决策', N'{"shortPeriod":"短期周期","longPeriod":"长期周期","signalPeriod":"信号周期"}', '{"shortPeriod":12,"longPeriod":26,"signalPeriod":9}', N'震荡指标策略', NOW(), NOW(), N'【使用场景】跨品种比较，相对强度分析。【优点】百分比形式便于比较不同价格水平股票。【缺点】本质仍为MACD变形，无突破性改进。【历史表现】夏普比率0.8-1.2，最大回撤15-25%。【评价】实用的标准化指标。'),
('DPO', N'区间震荡指标策略', N'基于区间震荡指标的超买超卖信号生成买卖决策', N'{"period":"计算周期"}', '{"period":20}', N'震荡指标策略', NOW(), NOW(), N'【使用场景】周期性市场分析，消除趋势影响。【优点】专注周期性波动，过滤趋势噪音。【缺点】不适合单边趋势市场。【历史表现】夏普比率0.5-0.9，最大回撤25-35%。【特色】周期分析专用工具。'),
('TRIX', N'三重指数平滑平均指标策略', N'基于三重指数平滑平均指标的超买超卖信号生成买卖决策', N'{"period":"计算周期","signalPeriod":"信号周期"}', '{"period":15,"signalPeriod":9}', N'震荡指标策略', NOW(), NOW(), N'【使用场景】中长线趋势判断，过滤短期噪音。【优点】三重平滑消除噪音，信号稳定。【缺点】滞后性较强，可能错过快速变化。【历史表现】夏普比率0.9-1.3，最大回撤12-20%。【评级】稳健的中长线指标。'),
-- 新增震荡指标策略
('FISHER', N'Fisher变换策略', N'基于Fisher变换的超买超卖信号生成买卖决策，将价格转换为正态分布', N'{"period":"计算周期"}', '{"period":10}', N'震荡指标策略', NOW(), NOW(), N'【使用场景】需要清晰超买超卖信号的交易。【优点】将价格转换为正态分布，信号更清晰。【缺点】计算复杂，对参数敏感。【历史表现】夏普比率0.8-1.4，最大回撤15-28%。【特色】数学变换的高级指标。'),
('FOSC', N'预测振荡器策略', N'基于预测振荡器的信号生成买卖决策，衡量当前价格与线性回归预测价格的偏差', N'{"period":"计算周期"}', '{"period":14}', N'震荡指标策略', NOW(), NOW(), N'【使用场景】价格预测，趋势偏离分析。【优点】基于线性回归预测，有一定前瞻性。【缺点】预测准确性有限，市场非线性特征明显。【历史表现】夏普比率0.6-1.0，最大回撤20-30%。【定位】预测辅助工具。'),
('EOM', N'移动便利性指标策略', N'基于移动便利性指标的信号生成买卖决策，衡量价格变动的难易程度', N'{"period":"计算周期","divisor":"除数"}', '{"period":14,"divisor":100000000}', N'震荡指标策略', NOW(), NOW(), N'【使用场景】结合价格和成交量的分析。【优点】综合考虑价格变动和成交量，信息更全面。【缺点】计算复杂，实时性不强。【历史表现】夏普比率0.7-1.1，最大回撤18-25%。【适用性】适合机构投资者。'),
('CHOP', N'震荡指数策略', N'基于震荡指数的信号生成买卖决策，衡量市场的震荡程度', N'{"period":"计算周期"}', '{"period":14}', N'震荡指标策略', NOW(), NOW(), N'【使用场景】市场状态识别，策略选择。【优点】明确区分趋势和震荡状态，指导策略选择。【缺点】本身不提供交易信号，需配合其他指标。【历史表现】作为过滤指标，可提升其他策略10-20%效果。【价值】策略筛选的重要工具。'),
